/*Note: No calls to server through ResponseAPI. User object passed from ListFragment*/
public class ProfileFragment extends Fragment {

    private FloatingActionButton btnMessage;
    private EditText txtName, txtDescription, txtAddress, txtDayRate, txtHourRate;
    private ImageView imgProfile;
    private User mUser;

    @Override
    public void onCreate(Bundle savedInstanceState)
    {
        super.onCreate(savedInstanceState);
    }

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState)
    {
        View v = inflater.inflate(R.layout.fragment_profile, container, false);
        //Define Objects
        mUser = new User(getActivity());
        mUser = (User)getActivity().getIntent().getSerializableExtra("User");
        mUser.print();
        imgProfile = (ImageView)container.findViewById(R.id.imgProfile);
        txtName = (EditText)container.findViewById(R.id.txtName);
        txtDescription = (EditText)container.findViewById(R.id.txtDescription);
        txtAddress = (EditText)container.findViewById(R.id.txtAddress);
        txtHourRate = (EditText)container.findViewById(R.id.txtHourRate);
        txtDayRate = (EditText)container.findViewById(R.id.txtDayRate);
        btnMessage = (FloatingActionButton) container.findViewById(R.id.btnMessage);

        try {
            //Fill Objects
            txtName.setText(mUser.getName());
            txtDescription.setText(mUser.getDescription());
            txtAddress.setText(mUser.getAddress());
            txtHourRate.setText(Double.toString(mUser.getHourRate()));
            txtDayRate.setText(Double.toString(mUser.getDayRate()));
            imgProfile.setImageBitmap(mUser.getImageAsBitmap());
        }catch (Exception ex){Log.v("ProfileFragment",ex.getMessage());}

        // Inflate the layout for this fragment
        return v;
    }

    public void onMessageClick(View view)
    {
        String str = "data:image/jpeg;base64,";
String ssp ="";
        String fragments ="";

//No content provider: data:image/png;base64,
        Bitmap bitmap;
        try {
            Uri imageUri = Uri.fromParts("data",str.replaceFirst("data:",""),"");
            bitmap = MediaStore.Images.Media.getBitmap(this.getActivity().getContentResolver(), imageUri);
            imgProfile.setImageBitmap(bitmap);
        }
        catch (Exception ex)
        {
            Log.v("User", ex.getMessage());
        }


        Snackbar.make(view, "Message sent", Snackbar.LENGTH_LONG).setAction("Action", null).show();
    }





}
